def row_spiral_forward(n: int):
    a = [[0]*n for _ in range(n)]
    top, bottom, left, right = 0, n-1, 0, n-1
    val = 1

    while top <= bottom and left <= right:
        for j in range(left, right+1):
            a[top][j] = val; val += 1
        top += 1

        for i in range(top, bottom+1):
            a[i][right] = val; val += 1
        right -= 1

        if top <= bottom:
            for j in range(right, left-1, -1):
                a[bottom][j] = val; val += 1
            bottom -= 1

        if left <= right:
            for i in range(bottom, top-1, -1):
                a[i][left] = val; val += 1
            left += 1

    return a

print(row_spiral_forward(4))

def row_spiral_reverse(n: int):
    a = [[0]*n for _ in range(n)]
    top, bottom, left, right = 0, n-1, 0, n-1
    val = n*n

    while top <= bottom and left <= right:
        for j in range(left, right+1):
            a[top][j] = val; val -= 1
        top += 1

        for i in range(top, bottom+1):
            a[i][right] = val; val -= 1
        right -= 1

        if top <= bottom:
            for j in range(right, left-1, -1):
                a[bottom][j] = val; val -= 1
            bottom -= 1

        if left <= right:
            for i in range(bottom, top-1, -1):
                a[i][left] = val; val -= 1
            left += 1

    return a

print(row_spiral_reverse(4))

def column_spiral_forward(n: int):
    a = [[0]*n for _ in range(n)]
    top, bottom, left, right = 0, n-1, 0, n-1
    val = 1

    while top <= bottom and left <= right:
        # Down along left column
        for i in range(top, bottom+1):
            a[i][left] = val; val += 1
        left += 1

        # Right along bottom row
        for j in range(left, right+1):
            a[bottom][j] = val; val += 1
        bottom -= 1

        # Up along right column
        if left <= right:
            for i in range(bottom, top-1, -1):
                a[i][right] = val; val += 1
            right -= 1

        # Left along top row
        if top <= bottom:
            for j in range(right, left-1, -1):
                a[top][j] = val; val += 1
            top += 1

    return a

print(column_spiral_forward(4))

def column_spiral_reverse(n: int):
    a = [[0]*n for _ in range(n)]
    top, bottom, left, right = 0, n-1, 0, n-1
    val = n*n

    while top <= bottom and left <= right:
        # Down along left column
        for i in range(top, bottom+1):
            a[i][left] = val; val -= 1
        left += 1

        # Right along bottom row
        for j in range(left, right+1):
            a[bottom][j] = val; val -= 1
        bottom -= 1

        # Up along right column
        if left <= right:
            for i in range(bottom, top-1, -1):
                a[i][right] = val; val -= 1
            right -= 1

        # Left along top row
        if top <= bottom:
            for j in range(right, left-1, -1):
                a[top][j] = val; val -= 1
            top += 1

    return a

print(column_spiral_reverse(4))
