def is_balanced(expr: str) -> bool:
    st = []
    pair = {')':'(', ']':'[', '}':'{'}

    for ch in expr:
        if ch in '([{':
            st.append(ch)
        elif ch in ')]}':
            if not st or st[-1] != pair[ch]:
                return False
            st.pop()

    return len(st) == 0

print(is_balanced("(a+b)*(c+d)"))
print(is_balanced("([)]"))
print(is_balanced("{[()]}"))

def infix_to_postfix(expr: str) -> str:
    prec = {'+': 1, '-': 1, '*': 2, '/': 2}
    out = []
    st = []

    expr = expr.replace(' ', '')

    def is_operand(ch):
        return ch.isalnum()

    for ch in expr:
        if is_operand(ch):
            out.append(ch)
        elif ch == '(':
            st.append(ch)
        elif ch == ')':
            while st and st[-1] != '(':
                out.append(st.pop())
            if st and st[-1] == '(':
                st.pop()
        else:
            # operator
            while st and st[-1] in prec and prec[st[-1]] >= prec[ch]:
                out.append(st.pop())
            st.append(ch)

    while st:
        out.append(st.pop())

    return ''.join(out)

print(infix_to_postfix("A+B*(C-D)"))
print(infix_to_postfix("(1+2)*3"))
print(infix_to_postfix("A*(B+C)"))

def eval_postfix(expr: str) -> int:
    st = []

    for ch in expr.replace(' ', ''):
        if ch.isdigit():
            st.append(int(ch))
        else:
            b = st.pop()
            a = st.pop()
            if ch == '+':
                st.append(a + b)
            elif ch == '-':
                st.append(a - b)
            elif ch == '*':
                st.append(a * b)
            elif ch == '/':
                # integer division for this version
                st.append(int(a / b))

    return st[0]

print(eval_postfix("231*+9-"))
print(eval_postfix("42/3+"))

def is_palindrome(word: str) -> bool:
    w = word.lower()
    st = []

    for ch in w:
        st.append(ch)

    for ch in w:
        if ch != st.pop():
            return False

    return True

print(is_palindrome("madam"))
print(is_palindrome("hello"))
print(is_palindrome("RaceCar"))
